import React from 'react';
import HomePage from './pages/homepage/homepage.comopnent';
import { Switch, Route} from 'react-router-dom';
import ShopPage from './pages/shop/shop.component';
import Header from './components/header/headre.component'
import './App.css';
import SignInUp from './pages/sign-in-sign-up/sign-in-sign-up.component'
import {auth} from './firebase/firebase.utils'




class App extends React.Component {

  constructor(){
    super();

    this.state={
        currentUser:null
    }
  }

  unsubscibeFromAuth=null

  componentDidMount(){
    this.unsubscibeFromAuth = auth.onAuthStateChanged(user => {
      this.setState({currentUser:user});
    } )
  }

  componentWillUnmount(){
    this.unsubscibeFromAuth()
  }




  render(){

    const {currentUser}= this.state;
  return (
    <div className="App">
    <Header currentUser>

    </Header>
    <Switch>
        <Route exact path='/' component ={HomePage}/>
        <Route path='/shop' component ={ShopPage}/>
        <Route path='/signin' component ={SignInUp}/>
    </Switch>

    </div>
  );
 }
}

export default App;
