import {createSelector} from 'reselect';

const selectUser= state => state.user;

const COLLECTION_ID_MAP={
    hats:1,
    sneakers:2,
    jackets:3,
    womens:4,
    mens:5

}

export const selectCurrentUser =createSelector(

    [selectUser],(user)=>user.currentUser
)
